import React, {useRef, useState} from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPlay,
  faAngleLeft,
  faAngleRight,
  faPause,
} from "@fortawesome/free-solid-svg-icons";

const Player = (props) => {
  //Ref
  const audioRef = useRef(null);//this is how you grab elements in React
  //event Handlers
  const playSongHandler = () =>{
    // `current` points to the mounted text input element
    if(props.isPlaying===true){
      audioRef.current.pause();
      props.setIsPlaying(false);
    }else{
      audioRef.current.play();
      props.setIsPlaying(true);
    }
  };
  const timeUpdateHandler = (e) =>{
    const current = e.target.currentTime;
    const duration = e.target.duration;
    setSongInfo({...songInfo, currentTime: current, duration: duration});

  };
  const getTime = (time) =>{
    return(
      Math.floor(time/ 60) + ":" + ("0" + Math.floor(time % 60 )).slice(-2)
    )

  }
  const dragHandler =(e)=>{
    audioRef.current.currentTime = e.target.value;
    setSongInfo({...songInfo, currentTime: e.target.value})

  };
  //State 
  const [songInfo, setSongInfo] = useState({
    currentTime: 0,
    duration: 0,
  })
  return (
    <div className="player">
      <div className="time-control">
        <p>{getTime(songInfo.currentTime)}</p>
        <input min={0} max={songInfo.duration} value={songInfo.currentTime} onChange={dragHandler} type="range" />
        <p>{getTime(songInfo.duration)}</p>
      </div>
      <div className="play-control">
        <FontAwesomeIcon className="skip-back" size="2x" icon={ faAngleLeft} />
        <FontAwesomeIcon onClick = {playSongHandler} className="play" size="2x" icon={props.isPlaying===false ?  faPlay: faPause} />

        <FontAwesomeIcon className="skip-forward" size="2x" icon={faAngleRight} />
      </div>
      <audio onTimeUpdate={timeUpdateHandler} onLoadedMetadata={timeUpdateHandler} ref={audioRef} src={props.currentSong.audio}></audio>
    </div>
  );
};
export default Player;
