import React from "react";

const LibrarySong = (props) => {
    const songSelectHandler = (e) =>{
        const selectedSong = props.songs.filter((state)=>state.id===props.id);//filter returns us an array
        console.log(e);
        props.setCurrentSong(selectedSong[0]);

        //check if song is playing
        if(props.isPlaying===true){
            const playPromise=props.audioRef.current.play();
            if(playPromise !== undefined){//if what we clicked on is undefined, we should wait a bit
                playPromise.then((audio)=>{// this is the wait, when it finally loads up we play it
                    props.audioRef.current.play();
                })
            }

        }
    };
  return (
    <div className={`library-song ${props.song.active ? 'selected' : ""}`} onClick={songSelectHandler}>
      <img alt={props.song.name} src={props.song.cover}></img>
      <div className="song-description">
        <h3> {props.song.name}</h3>
        <h4> {props.song.artist}</h4>
      </div>
    </div>
  );
};

export default LibrarySong;
